a!localVariables(
  /* Used to track if we are creating a new task or editing an existing one
  If there is an ID populated, this must be a preexisting task that is being edited/viewed */
  local!isExistingTaskCategory: rule!AS_CO_UT_isNotBlank(ri!selectedTaskCategory.taskCategoryId),
  {
    rule!AS_CO_DSP_displayHeader(
      headerText: rule!AS_GAM_CO_I18N_UT_displayLabel(
        bundle: ri!i18nData,
        bundleKey: if(
          local!isExistingTaskCategory,
          "lbl_EditCategory",
          "lbl_AddCategory"
        )
      )
    ),
    rule!AS_GSS_TMG_CP_warnCard(
      showWhen: local!isExistingTaskCategory,
      label: "Edits to this phase will be reflected on all tasks that reference it"
    ),
    rule!AS_GSS_TMG_CP_taskCategoryContents(
      i18nData: ri!i18nData,
      taskCategory: ri!selectedTaskCategory
    ),
    rule!AS_CO_CPS_cancelSaveButtons(
      confirmLabel: rule!AS_GAM_CO_I18N_UT_displayLabel(
        bundle: ri!i18nData,
        bundleKey: if(
          local!isExistingTaskCategory,
          "btn_SaveChanges",
          "btn_AddCategory"
        )
      ),
      cancelLabel: "Cancel",
      confirmSaveInto: {
        rule!AS_ADT_UT_startAuditProcess(
          newObjects: ri!selectedTaskCategory,
          objectType: 'type!{urn:com:appian:types:AS:GSS:TMG}AS_GSS_TMG_R_TaskCategory',
          objectDse: cons!AS_GSS_TMG_ENT_R_TASK_CATEGORY,
          auditDse: cons!AS_GSS_TMG_ENT_A_R_TASK_CATEGORY,
          auditConfig: rule!AS_GSS_TMG_ADT_BL_auditConfig_R_TaskCategory()
        ),
        rule!AS_CO_UT_triggerRefresh(triggerRefresh: ri!triggerRefresh),
        rule!AS_CO_UT_saveNull(valueToNull: ri!selectedTaskCategory)
      },
      cancelSaveInto: {
        rule!AS_CO_UT_saveNull(valueToNull: ri!selectedTaskCategory)
      },
      includeDivider: true
    )
  }
)
