sail_code:
a!localVariables(
  /* Used to track if we are creating a new category or editing an existing one
  If there is an ID populated, this must be a preexisting category that is being edited */
  /*Check to see if there is a different category with the same name in the DB*/
  local!isDuplicateTaskCategoryName: a!refreshVariable(
    value: rule!AS_GSS_TMG_UT_isDuplicateTaskCategoryName(taskCategory: ri!taskCategory),
    /*Don't want to update every time anything changes, just the name*/
    refreshOnReferencedVarChange: false,
    refreshOnVarChange: ri!taskCategory.categoryName
  ),
  {
    #"SYSTEM_SYSRULES_columnsLayout"(
      columns: {
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: {
            rule!AS_CO_INP_textField(
              label:"Phase Name",

              required: true,
              value: ri!taskCategory.categoryName,
              validations: {
                rule!AS_CO_VD_textValidation(
                  value: ri!taskCategory.categoryName,
                  minLength: 1,
                  maxLength: cons!AS_CO_ENUM_TEXT_LENGTH_LONG
                ),
                if(
                  local!isDuplicateTaskCategoryName,
                  "You cannot have two phases with the same name",

                  ""
                )
              },
              saveInto: a!save(
                target: ri!taskCategory.categoryName,
                value: save!value
              ),
              refreshAfter: "UNFOCUS",
              labelPosition: "ABOVE"
            )
          },
          width: "MEDIUM_PLUS"
        )
      }
    )
  }
)
