sail_code:
a!localVariables(
  local!isDuplicateTemplateName: rule!AS_GSS_TMG_UT_isDuplicateTemplateName(
    selectedTemplate: ri!selectedTemplate,
    /*triggerRefresh: ri!triggerRefresh,*/
    refTemplates: rule!AS_GSS_TMG_QE_getTemplate(
      templateName: ri!selectedTemplate.templateName,
      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
      templateTypeId: cons!AS_GSS_REF_ID_TEMPLATE_TYPE_CONTRACTING,
      triggerRefresh: ri!triggerRefresh
    ),
  ),
  {
    #"SYSTEM_SYSRULES_columnsLayout"(
      columns: {
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: {
            rule!AS_CO_INP_textField(
              label: "Approach Name",
              required: true,
              value: ri!selectedTemplate.templateName,
              saveInto: a!save(
                ri!selectedTemplate.templateName,
                save!value
              ),
              readOnly: ri!readOnly,
              validations: {
                rule!AS_CO_VD_textValidation(
                  value: ri!selectedTemplate.templateName,
                  maxLength: cons!AS_CO_ENUM_TEXT_LENGTH_MEDIUM
                ),
                rule!AS_CO_UT_validation(
                  validation: "You cannot have two approaches with the same name",
                  validateWhen: local!isDuplicateTemplateName
                )
              }
            )
          },
          width: "MEDIUM_PLUS"
        ),
        #"SYSTEM_SYSRULES_columnLayout"(contents: {}, width: "WIDE_PLUS")
      }
    ),
    #"SYSTEM_SYSRULES_sideBySideLayout"(
      items: {
        #"SYSTEM_SYSRULES_sideBySideItem"(
          item: rule!AS_CO_INP_paragraphField(
            label: "Description",
            value: ri!selectedTemplate.templateDesc,
            saveInto: a!save(
              ri!selectedTemplate.templateDesc,
              save!value
            ),
            readOnly: ri!readOnly,
            height: "SHORT",
            validations: {
              rule!AS_CO_VD_textValidation(
                value: ri!selectedTemplate.templateDesc,
                maxLength: cons!AS_CO_ENUM_TEXT_LENGTH_LONG
              )
            }
          ),
          width: "2X"
        ),
        #"SYSTEM_SYSRULES_sideBySideItem"(width: "MINIMIZE")
      }
    )
  }
)
