sail_code:
{
  /*remove task as precedent for other tasks*/
  if(
    rule!AS_CO_UT_isBlank(ri!selectedTemplate.templateTasks),
    a!save(
      ri!selectedTemplate,
      ri!selectedTemplate
    ),
    #"SYSTEM_SYSRULES_forEach"(
      items: ri!selectedTemplate.templateTasks,
      expression: a!save(
        fv!item.templateTaskPrecedents,
        rule!AS_CO_UT_rejectCdtByField(
          cdt: fv!item.templateTaskPrecedents,
          field: "taskRefId_precedent",
          value: ri!taskRefIdToRemove
        )
      )
    )
  ),
  /*remove task from template*/
  a!save(
    ri!selectedTemplate.templateTasks,
    rule!AS_CO_UT_rejectCdtByField(
      cdt: ri!selectedTemplate.templateTasks,
      field: "taskRef.taskRefId",
      value: ri!taskRefIdToRemove
    )
  )
}
