sail_code:
a!localVariables(
  local!searchText: rule!AS_CO_UT_initializeBlankLocalVariable(
    type: 'type!{http://www.appian.com/ae/types/2009}Text'
  ),
  {
    #"SYSTEM_SYSRULES_columnsLayout"(
      columns: {
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: {
            rule!AS_CO_DSP_displayHeader(
              headerText: rule!AS_GAM_CO_I18N_UT_displayLabel(
                bundle: ri!i18nData,
                bundleKey: "lbl_SelectTasks",
                arguments: ri!addingTaskCategory.categoryName
              ),
              headerLevel: ri!headerLevel
            ),
            #"SYSTEM_SYSRULES_ButtonArrayLayout"(
              buttons: {
                rule!AS_CO_BTN_toolbarButton(
                  label: "Add Custom Task",
                  saveInto: {
                    a!save(
                      ri!newCustomTask.taskCategory,
                      ri!addingTaskCategory
                    )
                  },
                  icon: "PLUS"
                )
              },
              showWhen: ri!showAddCustomTaskBtn,
              align: "START"
            )
          }
        ),
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: rule!AS_CO_CP_searchField(
            value: local!searchText,
            saveInto: local!searchText,
            placeholder: rule!AS_GSS_TMG_pickerPlaceholder(
              i18nData: ri!i18nData,
              bundleKey: "lbl_Tasks"
            )
          ),
          width: "MEDIUM"
        )
      },
      alignVertical: if(ri!showAddCustomTaskBtn, "BOTTOM", null),
      marginBelow: "STANDARD"
    ),
    rule!AS_GSS_TMG_GRD_referenceTaskGrid(
      searchText: local!searchText,
      allowLinkSelection: false,
      selectedTaskRefs: ri!selectedTaskRefs,
      i18nData: ri!i18nData,
      allowCheckboxSelection: true,
      taskCategoryFilter: ri!addingTaskCategory,
      taskRefIdsToHide: ri!taskRefIdsToHide,
      triggerRefresh: ri!triggerRefresh,
      configurationLevelCode: cons!AS_GSS_TMG_ENUM_TASK_CONFIGURATION_LEVEL_CODE_TEMPLATE
    ),
    rule!AS_CO_CPS_cancelSaveButtons(
      cancelAccessibilityText: "Press Enter to cancel changes",
      confirmAccessibilityText: "Press Enter to add items to process",
      confirmLabel: "Add Tasks",
      cancelLabel: "Cancel",
      confirmSaveInto: {
        ri!addTaskSaves,
        rule!AS_CO_UT_saveNull(valueToNull: ri!addingTaskCategory),
        rule!AS_CO_UT_saveNull(valueToNull: ri!selectedTaskRefs)
      },
      cancelSaveInto: {
        rule!AS_CO_UT_saveNull(valueToNull: ri!addingTaskCategory),
        rule!AS_CO_UT_saveNull(valueToNull: ri!selectedTaskRefs)
      },
      includeDivider: true
    )
  }
)
