sail_code:
a!localVariables(
  local!i18nData: rule!AS_CO_I18N_UT_loadBundleFromFolder(
    bundleFolder: cons!AS_ADT_FLD_INTERNATIONALIZATION_FILES,
    bundleNames: "AS.ADT.AuditFieldLevel"
  ),

  #"SYSTEM_SYSRULES_forEach"(
    items: {
      {
        code: cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_ADD,
        /*green*/
        color: "#6ba547",
        bundleKey: "AS.ADT.AuditFieldLevel.lbl_Added"
      },
      {
        code: cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_DELETE,
        /*red*/
        color: "#e64345",
        bundleKey: "AS.ADT.AuditFieldLevel.lbl_Deleted"
      },
      {
        code: cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_MODIFY,
        /*purple*/
        color: "#b77ea3",
        bundleKey: "AS.ADT.AuditFieldLevel.lbl_Modified"
      },
      {
        code: cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_DEACTIVATE,
        /*orange*/
        color: "#e48f1b",
        bundleKey: "AS.ADT.AuditFieldLevel.lbl_Deactivated"
      },
      {
        code: cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_REACTIVATE,
        /*blue*/
        color: "#1925e3",
        bundleKey: "AS.ADT.AuditFieldLevel.lbl_Reactivated"
      },
      {
        code: cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_UNCHANGED,
        /*gray*/
        color: "#808080",
        bundleKey: "AS.ADT.AuditFieldLevel.lbl_Unchanged"
      }
    },
    expression: {
      code: tostring(fv!item.code),
      color: fv!item.color,
      label: rule!AS_CO_I18N_UT_displayLabel(
        i18nData: local!i18nData,
        bundleKey: fv!item.bundleKey
      )
    }
  )
)
