a!localVariables(
  local!taskRefData: rule!AS_GSS_TMG_QE_getTaskRefs(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY
  ),
  {
    rule!AS_CO_DSP_backLink(
      label: rule!AS_GAM_CO_I18N_UT_displayLabel(
        bundle: ri!i18nData,
        bundleKey: ri!backBundleKey
      ),
      saveInto: {
        a!save(ri!showHistory, false),
        rule!AS_CO_UT_saveNull(valueToNull: ri!selectedTemplate)
      }
    ),
    rule!AS_CO_DSP_displayHeader(
      headerText: ri!selectedTemplate.templateName,
      headerLevel: cons!AS_CO_ENUM_HEADER_LEVEL_ONE,
      align: "LEFT"
    ),
    rule!AS_CO_DSP_displayHeader(
      headerText: rule!AS_GAM_CO_I18N_UT_displayLabel(
        bundle: ri!i18nData,
        bundleKey: ri!subHeaderBundleKey
      ),
      headerLevel: cons!AS_CO_ENUM_HEADER_LEVEL_TWO,
      align: "LEFT"
    ),
    rule!AS_GSS_TMG_GRD_templateChangesAudit(
      i18nData: ri!i18nData,
      selectedTemplate: ri!selectedTemplate,
      taskRefData: local!taskRefData,
      triggerRefresh: ri!triggerRefresh
    )
  }
)
