sail_code:
if(
  and(
    rule!AS_CO_UT_booleanDefaultFalse(ri!firstIsDefault),
    rule!AS_CO_UT_booleanDefaultFalse(ri!lastIsDefault)
  ),
  fn!error(
    "firstIsDefault and lastIsDefault cannot both be true"
  ),
  if(
    and(
      or(ri!firstIsDefault, ri!lastIsDefault),
      rule!AS_CO_UT_isNotBlank(ri!customDefaults)
    ),
    fn!error(
      "customDefault will never be used and should not be passed when either firstIsDefault or lastIsDefault is true"
    ),
    #"SYSTEM_SYSRULES_forEach"(
      items: ri!values,
      expression: rule!AS_CO_UT_displayValue(
        value: fv!item,
        inArray: ri!inArray,
        replacement: ri!replacement,
        firstIsDefault: ri!firstIsDefault,
        lastIsDefault: ri!lastIsDefault,
        customDefault: index(ri!customDefaults, fv!index, null)
      )
    )
  )
)
