sail_code:
{
  #"SYSTEM_SYSRULES_gridField_v1"(
    labelPosition: "COLLAPSED",
    data: a!localVariables(
      local!auditTemplates: a!refreshVariable(
        value: #"a3284a41-31d5-4d7a-858a-84d17c3c74e9-tmg-am-am"(
          pagingInfo: fv!pagingInfo,
          templateId: ri!selectedTemplate.templateId,
          triggerRefresh:ri!triggerRefresh
        ).data,
        refreshOnReferencedVarChange: true
      ),
      local!templateAuditDataForGrid: a!refreshVariable(
        value: a!flatten(
          #"SYSTEM_SYSRULES_forEach"(
            items: local!auditTemplates,
            expression: #"c26cc0cb-4d83-474e-81f3-2e5daa662025-tmg-am-am"(auditTemplate: fv!item)
          )
        ),
        refreshOnReferencedVarChange: true
      ),
      todatasubset(
        local!templateAuditDataForGrid,
        fv!pagingInfo
      )
    ),
    columns: {
      rule!AS_CO_UI_userImageAndDisplayNameGridColumns(
        usernameField: "username",
        label: "User",
        allowSorting: false
      ),
      /*Modification*/
      #"SYSTEM_SYSRULES_gridColumn"(
        label: "Modification",
        value: rule!AS_GSS_TMG_DSP_modificationCommentForTemplateAudit(
          i18nData: ri!i18nData,
          templateAuditEntry: fv!row,
          selectedTemplate: ri!selectedTemplate,
          taskRefData: ri!taskRefData
        ),
        width: "WIDE"
      ),
      /*Time*/
      #"SYSTEM_SYSRULES_gridColumn"(
        label: "Time",
        value: rule!AS_CO_UT_formatDateTime(datetime: fv!row.timestamp),
        sortField: "timestamp",
        align: "END"
      )
    },
    borderStyle: "LIGHT",
    shadeAlternateRows: true,
    initialSorts: #"SYSTEM_SYSRULES_sortInfo"(field: "timestamp", ascending: false),
    pageSize: cons!AS_CO_ENUM_PAGE_SIZE_LARGE
  )
}
