sail_code:
choose(
  rule!AS_CO_UT_chooseWherecontainsHelper(
    value: ri!fieldName,
    inArray: rule!AS_GSS_TMG_CONS_templateAuditFields()
  ),
  /*Default*/
  "",
  /*Template Name*/
  rule!AS_CO_UT_wrapTextInQuotes(text: ri!value),
  /*Template Description*/
  rule!AS_CO_UT_wrapTextInQuotes(text: ri!value),
  /*Group assignee*/
  rule!AS_CO_UT_wrapTextInQuotes(
    text: rule!AS_CO_UT_displayGroupOrUser(togroup(ri!value))
  ),
  /*Task Description*/
  rule!AS_CO_UT_wrapTextInQuotes(text: ri!value),
  /*Task*/
  rule!AS_CO_UT_wrapTextInQuotes(
    text: index(
      ri!taskRef,
      wherecontains(
        tointeger(ri!value),
        tointeger(ri!taskRef.taskRefId)
      ),
      null
    ).taskName
  ),
  /*Task ref id Precedents*/
  rule!AS_CO_UT_wrapTextInQuotes(
    text: index(
      ri!taskRef,
      wherecontains(
        tointeger(ri!value),
        tointeger(ri!taskRef.taskRefId)
      ),
      null
    ).taskName
  ),
  /*Group id Precedents*/
  rule!AS_CO_UT_wrapTextInQuotes(
    text: if(
      rule!AS_CO_UT_isBlank(ri!value),
      "",
      group(togroup(ri!value), "groupName")
    )
  ),
  /*Duration*/
  ri!value,
  /*Duration unit*/
  index(
    ri!refData,
    wherecontains(
      tointeger(ri!value),
      tointeger(ri!refData.refDataId)
    ),
    null
  ).refLabel,
  /*No of days*/
  ri!value,
  /*Operation*/
  index(
    ri!refData,
    wherecontains(
      tointeger(ri!value),
      tointeger(ri!refData.refDataId)
    ),
    null
  ).refLabel,
  /*Threshold Amount*/
  rule!AS_CO_UT_formatCurrencyValue(
    currencyValue: ri!value,
    showDecimal: true
  ),
  /*Document Template*/
  index(
    ri!refDocTemplates,
    wherecontains(
      tointeger(ri!value),
      tointeger(ri!refDocTemplates.documentTemplateId)
    ),
    null
  ).documentName
)
