sail_code:
cast(
  typeof(ri!cdt),
  if(
    or(
      rule!AS_CO_UT_isBlank(ri!value),
      rule!AS_CO_UT_isBlank(ri!cdt),
    ),
    ri!cdt,
    remove(
      a!flatten(ri!cdt),
      rule!AS_CO_UT_returnCdtIndicesByFieldIncludes(
        cdt: ri!cdt,
        field: ri!field,
        value: ri!value
      )
    )
  )
)
