sail_code:
cast(
  typeof(ri!listOne),
  if(
    or(rule!AS_CO_UT_isBlank(ri!listOne), rule!AS_CO_UT_isBlank(ri!listTwo)),
    ri!listOne,
    #"SYSTEM_SYSRULES_forEach"(
      items: ri!listOne,
      expression: rule!AS_CO_UT_replaceNull(
        nullableValue: index(
          rule!AS_CO_UT_filterCdtByField(
            cdt: ri!listTwo,
            field: ri!field,
            value: fv!item[ri!field]
          ),
          1,
          null
        ),
        replacementValue: fv!item
      )
    )
  )
)
