sail_code:
/*Disable MARK NOT NEEDED when the Selected TAsk is Not AD HOC and CHECKLIST SET UP (CO groups)*/
or(
  #"SYSTEM_SYSRULES_forEach"(
    items: ri!tasks,
    expression: and(
      fv!item.taskBehaviorType.configurationLevelCode <> cons!AS_GSS_TMG_ENUM_TASK_CONFIGURATION_LEVEL_CODE_AD_HOC,
      if(
        fv!item.taskCategory.categoryName <> cons!AS_GSS_TMG_ENUM_TASK_CATEGORY_CHECKLIST_SETUP,
        true,
        not(ri!isContractingPersonnel)
      )
    )
  )
)
