sail_code:
a!localVariables(
  local!maxPerRow: rule!AS_CO_UT_replaceNull(nullableValue: ri!maxPerRow, replacementValue: count(ri!cardLayouts)),

  #"SYSTEM_SYSRULES_forEach"(
    items: enumerate(ceiling(count(ri!cardLayouts)/local!maxPerRow, 1)),
    expression: a!localVariables(
      local!rowIndex: fv!index,
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(showWhen: contains({"RIGHT", "CENTER"}, ri!align)),
          #"SYSTEM_SYSRULES_forEach"(
            items: enumerate(local!maxPerRow),
            expression: a!localVariables(
              local!adjustedIndex: (local!rowIndex - 1) * local!maxPerRow + fv!index,
              local!cardLayout: a!refreshVariable(
                value: index(ri!cardLayouts, local!adjustedIndex, {}),
                refreshAlways: true
              ),
              #"SYSTEM_SYSRULES_columnLayout"(
                width: ri!columnWidth,
                contents: local!cardLayout,
                showWhen: if(
                  and(
                    local!rowIndex = 1,
                    rule!AS_CO_UT_isBlank(local!cardLayout)
                  ),
                  false,
                  true
                )
              )
            )
          ),
          #"SYSTEM_SYSRULES_columnLayout"(showWhen: contains({"LEFT", "CENTER"}, ri!align))
        }
      )
    )
  )
)
