sail_code:
a!localVariables(
  local!i18nData: rule!AS_GSS_CO_UT_loadBundleFromFolder(langISOCode: null),
  /* Remove the currently selected "Welcome" subAction from the card navigation options */
  local!subActionsForCards: rule!AS_CO_UT_rejectCdtByField(
    cdt: rule!AS_GSS_TMG_BL_taskManagementOptions()[1].subActions,
    field: "image",
    value: null
  ),
  {
    #"SYSTEM_SYSRULES_sectionLayout_v1"(
      label: "Welcome to Source Selection Settings",
      labelColor: "STANDARD",
      labelHeadingTag: "H1",
      marginAbove: "LESS",
      marginBelow: "STANDARD"
    ),
    rule!AS_CO_CPS_cardsAsNavigation(
      choiceLabels: local!subActionsForCards.label,
      choiceValues: local!subActionsForCards.subActionId,
      choiceImages: local!subActionsForCards.image,
      maxPerRow: 3,
      choiceInstructions: local!subActionsForCards.instructions,
      choiceLinks: #"SYSTEM_SYSRULES_forEach"(
        items: local!subActionsForCards.subActionId,
        expression: #"SYSTEM_SYSRULES_startProcessLink"(
          processModel: cons!AS_GSS_TMG_PM_TASK_MANAGEMENT,
          processParameters: { selectedSubActionId: fv!item }
        )
      )
    )
  }
)
