sail_code:
#"SYSTEM_SYSRULES_gridField_v1"(
  labelPosition: "COLLAPSED",
  data: rule!AS_GSS_TMG_QE_getTemplate(
    excludeNestedCdts: false,
    pagingInfo: fv!pagingInfo,
    searchText: ri!searchText,
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
    triggerRefresh: ri!triggerRefresh,
    templateTypeId: cons!AS_GSS_REF_ID_TEMPLATE_TYPE_DOCUMENT_REVIEW
  ),
  columns: {
    /*Name*/
    #"SYSTEM_SYSRULES_gridColumn"(
      label: "Review Name",
      sortField: "templateName",
      helpTooltip: "Click a review name to edit",
      value: #"SYSTEM_SYSRULES_richTextDisplayField"(
        value: #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: fv!row.templateName,
          link: #"SYSTEM_SYSRULES_dynamicLink"(
            /*requery upon click to load with nested cdts included*/
            value: rule!AS_GSS_TMG_QE_getTemplate(
              templateId: fv!row.templateId,
              excludeNestedCdts: false,
              returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
              triggerRefresh: ri!triggerRefresh,
              templateTypeId: cons!AS_GSS_REF_ID_TEMPLATE_TYPE_DOCUMENT_REVIEW
            ),
            saveInto: ri!selectedReview
          ),
          linkStyle: "STANDALONE",
          style: "STRONG"
        )
      )
    ),
    /*Doc template*/
    #"SYSTEM_SYSRULES_gridColumn"(
      label: "Document Template",
      sortField: "docTemplate.documentName",
      value: fv!row.docTemplate.documentName
    ),
    /*Status*/
    #"SYSTEM_SYSRULES_gridColumn"(
      label: "Status",
      sortField: "isActive",
      value: displayvalue(
        fv!row.isActive,
        { true, false },
        { "Active", "Inactive" },
        ""
      )
    ),
    /*Last Modified*/
    #"SYSTEM_SYSRULES_gridColumn"(
      label: "Last Modified",
      sortField: "modifiedDatetime",
      value: rule!AS_CO_DSP_displayTimeByUser(
        user: fv!row.modifiedBy,
        dateTime: fv!row.modifiedDatetime
      )
    ),
    /*History*/
    #"SYSTEM_SYSRULES_gridColumn"(
      value: #"SYSTEM_SYSRULES_richTextDisplayField"(
        value: #"SYSTEM_SYSRULES_richTextIcon"(
          icon: "history",
          altText: "View review history",
          caption: "View review history",
          link: #"SYSTEM_SYSRULES_dynamicLink"(
            value: rule!AS_GSS_TMG_QE_getTemplate(
              templateId: fv!row.templateId,
              excludeNestedCdts: false,
              templateTypeId: cons!AS_GSS_REF_ID_TEMPLATE_TYPE_DOCUMENT_REVIEW,
              returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
              triggerRefresh: ri!triggerRefresh
            ),
            saveInto: {
              a!save(ri!selectedReview, save!value),
              a!save(ri!showHistory, true)
            }
          ),
          linkStyle: "STANDALONE",
          size: "MEDIUM"
        ),
        accessibilityText: "Click here to view review history"
      ),
      align: "CENTER",
      width: "ICON"
    )
  },
  pageSize: cons!AS_CO_ENUM_PAGE_SIZE_LARGE,
  initialSorts: {
    #"SYSTEM_SYSRULES_sortInfo"(field: "templateName", ascending: true)
  },
  secondarySorts: {
    #"SYSTEM_SYSRULES_sortInfo"(field: "templateName", ascending: true)
  }
)
