sail_code:
{
  /*remove task as precedent for other tasks*/
  if(
    rule!AS_CO_UT_isBlank(ri!selectedReview.templateTasks),
    a!save(ri!selectedReview, ri!selectedReview),
    #"SYSTEM_SYSRULES_forEach"(
      items: ri!selectedReview.templateTasks,
      expression: a!save(
        fv!item.templateTaskPrecedents,
        rule!AS_CO_UT_rejectCdtByField(
          cdt: fv!item.templateTaskPrecedents,
          field: "groupId_precedent",
          value: ri!groupIdToRemove
        )
      )
    )
  ),
  /*remove task from template*/
  a!save(
    ri!selectedReview.templateTasks,
    remove(
      ri!selectedReview.templateTasks,
      ri!templateTaskIndexToRemove
    )
  )
}
