a!localVariables(
  local!refData: rule!AS_GSS_QE_getRefDataByTypes(
    refTypes: cons!AS_GSS_REF_TYPE_EVALUATION_STATUS,
    excludeRefDataIds: {
      cons!AS_GSS_REF_ID_EVALUATION_STATUS_TEMP,
      cons!AS_GSS_REF_ID_EVALUATION_STATUS_DELETED
    }
  ),
  #"SYSTEM_SYSRULES_recordFilterList"(
    name: "Status",
    options: #"SYSTEM_SYSRULES_forEach"(
      items: local!refData,
      expression: #"SYSTEM_SYSRULES_recordFilterListOption"(
        id: fv!item.refDataId,
        name: fv!item.refLabel,
        filter: #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!AS_GSS_Evaluation_RECORD.evaluationStatus.refDataId,
          operator: "=",
          value: fv!item.refDataId
        )
      )
    ),
    allowMultipleSelections: true
  )
)
