sail_code:
a!localVariables(
  local!percentage: if(
    or(
      todecimal(ri!itemsCompleted) = 0,
      rule!AS_CO_UT_isBlank(ri!itemsCompleted)
    ),
    "-",
    if(
      or(
        todecimal(ri!totalCount) = 0,
        rule!AS_CO_UT_isBlank(ri!totalCount)
      ),
      0,
      (ri!itemsCompleted / ri!totalCount) * 100
    )
  ),
  local!i18nData: rule!AS_GSS_CO_UT_loadBundleFromFolder(null()),
  #"SYSTEM_SYSRULES_cardLayout"(
    contents: {
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              #"SYSTEM_SYSRULES_gaugeField"(
                label: "Gauge",
                labelPosition: "COLLAPSED",
                percentage: local!percentage,
                primaryText: #"SYSTEM_SYSRULES_gaugePercentage"(),
                color: "ACCENT",
                size: "SMALL",
                accessibilityText: concat(
                  ri!accessibilityTextForGauge & " (",
                  local!percentage,
                  "%)"
                )
              ),
              #"SYSTEM_SYSRULES_richTextDisplayField"(
                labelPosition: "COLLAPSED",
                value: {
                  #"SYSTEM_SYSRULES_richTextItem_v1"(
                    text: { ri!itemsCompleted },
                    color: "ACCENT",
                    size: "MEDIUM",
                    style: { "STRONG" }
                  ),
                  #"SYSTEM_SYSRULES_richTextItem_v1"(
                    text: {
                      #"SYSTEM_SYSRULES_richTextItem_v1"(text: { " " }, style: { "STRONG" }),
                      #"SYSTEM_SYSRULES_richTextItem_v1"(
                        text: { "/ " & ri!totalCount & " " },
                        size: "SMALL"
                      )
                    },
                    color: "SECONDARY"
                  ),
                  #"SYSTEM_SYSRULES_richTextItem_v1"(
                    text: ri!additionalText,
                    size: "SMALL",
                    color: "SECONDARY"
                  )
                },
                align: "CENTER",
                accessibilityText: ri!accessibilityTextForItem,
                showWhen: and(
                  rule!AS_CO_UT_isNotBlank(ri!itemsCompleted),
                  rule!AS_CO_UT_isNotBlank(ri!totalCount)
                )
              )
            }
          )
        }
      )
    },
    height: "AUTO",
    style: "NONE",
    padding: "LESS",
    marginBelow: "STANDARD",
    showBorder: false
  )
)
