/* Direct mapping handles all the nested CDT relationships since the data types matches. 
Else need to construct and pass if needed.*/
if(
  rule!AS_CO_UT_isBlank(ri!refVendor),
  null,
  rule!AS_CO_UT_captureMetadata(
    cdt: rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
      cdt: ri!vendor,
      fieldValuePairs: {
        {
          field: "vendorRefId",
          value: ri!refVendor.vendorRefId
        },
        {
          field: "evaluation.evaluationId",
          value: ri!evaluationId
        },
        {
          field: "legalName",
          value: ri!refVendor.legalName
        },
        {
          field: "businessType",
          value: ri!refVendor.businessType
        },
        {
          field: "vendorAddressLine1",
          value: ri!refVendor.vendorAddressLine1
        },
        {
          field: "vendorAddressLine2",
          value: ri!refVendor.vendorAddressLine2
        },
        {
          field: "vendorCity",
          value: ri!refVendor.vendorCity
        },
        {
          field: "vendorState",
          value: ri!refVendor.vendorState
        },
        {
          field: "vendorCountry",
          value: ri!refVendor.vendorCountry
        },
        {
          field: "vendorZipCode",
          value: ri!refVendor.vendorZipCode
        },
        {
          field: "vendorZipCodeExt",
          value: ri!refVendor.vendorZipCodeExt
        },
        {
          field: "vendorForeignPostalCode",
          value: ri!refVendor.vendorForeignPostalCode
        },
        { field: "duns", value: ri!refVendor.duns },
        {
          field: "cageCode",
          value: ri!refVendor.cageCode
        },
        {
          field: "uniqueEntityId",
          value: ri!refVendor.uniqueEntityId
        },
        {
          field: "status",
          value: ri!refVendor.status
        },
        {
          field: "expirationDate",
          value: ri!refVendor.expirationDate
        },
        {
          field: "submittedDate",
          value: ri!submissionDate
        }
      }
    ),
    modifiedBy: ri!initiator
  )
)
