sail_code:
if(
  rule!AS_CO_UT_isBlank(
    index(
      ri!vendorDocs,
      wherecontains(false, ri!vendorDocs.isDeleted),
      null
    )
  ),
  /*          Tag line      */
  rule!AS_GSS_UI_vendorGrid_docTagLine(i18nData: ri!i18nData, align: "START"),
  rule!AS_GSS_CPS_richTextListWithShowMoreLess(
    label: "Vendor Documents",
    labelPosition: "COLLAPSED",
    align: "LEFT",
    items: #"SYSTEM_SYSRULES_forEach"(
      items: index(
        ri!vendorDocs,
        wherecontains(false, ri!vendorDocs.isDeleted),
        null
      ),
      expression: {
        #"SYSTEM_SYSRULES_richTextIcon"(
          icon: "file-text-o",
          size: "STANDARD",
          color: "ACCENT"
        ),
        #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: " " & fv!item.documentName & " ",
          style: "STRONG",
          link: #"SYSTEM_SYSRULES_documentDownloadLink"(
            document: fv!item.appianDocId,
            label: fv!item.documentName
          ),
          linkStyle: "STANDALONE"
        ),
        char(10),
        #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: if(
            rule!AS_CO_UT_isBlank(ri!factorDocumentMappingData),
            "0 Factors, 0 Subfactors",
            rule!AS_GAM_CO_I18N_UT_displayLabel(
              bundle: ri!i18nData,
              bundleKey: "txt_FactorsandSubfactors",
              arguments: {
                tointeger(
                  index(
                    ri!factorDocumentMappingData[wherecontains(
                      tointeger(fv!item.evalDocumentId), tointeger(
                        ri!factorDocumentMappingData.evalDocumentId
                      )
                    )],
                    "factors",
                    0
                  )
                ),
                index(
                  ri!factorDocumentMappingData[wherecontains(
                    tointeger(fv!item.evalDocumentId), tointeger(
                      rule!AS_CO_UT_indexPath(
                        cdt: ri!factorDocumentMappingData, field: "evalDocumentId", indexDefault: {}
                      )
                    )
                  )],
                  "subFactors",
                  0
                )
              }
            ),

          ),
          style: "PLAIN",
          color: "SECONDARY"
        ),
        if(
          ri!isDeletable,
          #"SYSTEM_SYSRULES_richTextIcon"(
            icon: "trash-o",
            link: #"SYSTEM_SYSRULES_dynamicLink"(
              value: fv!item.evalDocumentId,
              saveInto: {
                a!save(
                  ri!vendorDocs[wherecontains(
                    fv!item.evalDocumentId, ri!vendorDocs.evalDocumentId
                  )].isDeleted,
                  true
                )
              },
              showWhen: ri!isDeletable
            ),
            linkStyle: "STANDALONE"
          ),
          {}
        ),
        char(10),
        char(10)
      }
    ),
    shortenIfLongerThanCount: cons!AS_GSS_MAX_LIMIT_FOR_DOCUMENT_DISPLAY,
    includeLineBreak: true(),
    showBulletedList: false(),
    i18nData: ri!i18nData
  )
)
