/*No test cases added, as this rule returns reaction tree*/
if(
  cons!AS_GSS_TOGGLE_CONSENSUS_FORM_AI,
  a!localVariables(
    local!httpResponse,
    local!responseFormattedText: rule!AS_GSS_BL_formatConsensusResponses_ToSuggestFinalCommentByAI(
      consensusResponses: ri!consensusResponses,
      vendor: ri!vendor
    ),
    {
      a!save(
        local!httpResponse,
        rule!AS_GSS_INT_summarize_ConsensusResponsesOrFinalComment(
          responseFormattedText: local!responseFormattedText,
          prompt: cons!AS_GSS_SUGGEST_FINAL_COMMENT_PROMPT
        )
      ),
      a!save(
        ri!consensusAiSuggestion,
        'type!{urn:com:appian:types}AS_GSS_ConsensusAiSuggestion'(
          consensusId: index(
            rule!AS_CO_UT_rejectNullValuesFromArray(
              array: ri!consensusResponses.consensusId
            ),
            1,
            null
          ),
          prompt: local!responseFormattedText,
          response: if(
            a!isNullOrEmpty(local!httpResponse.result),
            null,
            #"SYSTEM_SYSRULES_toJson_v1"(local!httpResponse.result)
          ),
          errorInfo: if(
            a!isNullOrEmpty(index(local!httpResponse, "error", null)),
            null,
            #"SYSTEM_SYSRULES_toJson_v1"(
              value: rule!AS_CO_UT_castToDictionary(
                value: index(local!httpResponse, "error", null)
              )
            )
          ),
          isSuccess: local!httpResponse.success,
          createdBy: loggedInUser(),
          createdDateTime: now()
        )
      ),
      #"SYSTEM_SYSRULES_writeToDataStoreEntity"(
        dataStoreEntity: cons!AS_GSS_ENT_CONSENSUS_AI_SUGGESTION,
        valueToStore: ri!consensusAiSuggestion
      ),
      if(
        local!httpResponse.success,
        rule!AS_GSS_MTR_SAVE_consensusFinalCommentsAiSuggestionGenerationSuccess(),
        rule!AS_GSS_MTR_SAVE_consensusFinalCommentsAiSuggestionGenerationFailure()
      )
    }
  ),
  {}
)
