sail_code:
/* This rule cannot have a test case */
a!localVariables(
  local!bundle: rule!AS_GSS_CO_UT_loadBundleFromFolder(langISOCode: null),
  local!filterValues: {
    a!map(
      label: "All Assignees",
      key: "ASSIGNEE_ALL"
    ),
    a!map(
      label: "Assigned to me",
      key: "ASSIGNEE_ME"
    ),
    a!map(
      label: "Unassigned",
      key: "UNASSIGNED"
    )
  },
  #"SYSTEM_SYSRULES_recordFilterList"(
    name: "Assignee",
    options: {
      #"SYSTEM_SYSRULES_recordFilterListOption"(
        id: 1,
        name: local!filterValues[1].label,
        filter: {
          /*Adding filter for taskId to be not null because we need to get all data for a record*/
          #"SYSTEM_SYSRULES_queryFilter"(
            field: recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskId,
            operator: "not null",

          )
        }
      ),
      #"SYSTEM_SYSRULES_recordFilterListOption"(
        id: 2,
        name: local!filterValues[2].label,
        filter: {
          #"SYSTEM_SYSRULES_queryFilter"(
            field: recordType!AS_GSS_TMG_Task_SYNCEDRECORD.userAssignee,
            operator: "=",
            value: ri!user
          )
        }
      ),
      #"SYSTEM_SYSRULES_recordFilterListOption"(
        id: 3,
        name: local!filterValues[3].label,
        filter: {
          #"SYSTEM_SYSRULES_queryFilter"(
            field: recordType!AS_GSS_TMG_Task_SYNCEDRECORD.userAssignee,
            operator: "is null"
          )
        }
      )
    },
    allowMultipleSelections: false,
    defaultOption: "All Assignees"
  )
)
