  or(
    rule!AS_CO_UT_isNotBlank(ri!stateCode),
    rule!AS_CO_UT_isNotBlank(ri!stateId)
  ),
  a!localVariables(
    local!state: rule!AS_GSS_QR_getRefState(
      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
      stateCode: ri!stateCode,
      stateId: ri!stateId
    ),
    if(
      rule!AS_CO_UT_isBlank(local!state),
      null,
      local!state
    )
  ),
  null
)
