sail_code:
if(
  or(rule!AS_CO_UT_isNotBlank(ri!countryCode),rule!AS_CO_UT_isNotBlank(ri!countryId)),
  a!localVariables(
    local!country: rule!AS_GSS_QR_getRefCountry(
      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
      countryCode: ri!countryCode,
      countryId: ri!countryId
    ),
    if(
      rule!AS_CO_UT_isBlank(local!country),
      null,
      local!country
    )
  ),
  null
)
