if(
  rule!AS_CO_UT_checkIsNullOrEmpty(
    value: index(
      ri!refVendors,
      recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.legalName,
      null
    )
  ),
  rule!AS_CO_UT_rejectNullValuesFromArray(array: ri!apiVendors),
  rule!AS_CO_UT_rejectNullValuesFromArray(
    array: a!flatten(
      {
        ri!refVendors,
        if(
          rule!AS_CO_UT_checkIsNullOrEmpty(
            value: index(
              ri!apiVendors,
              recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.legalName,
              null
            )
          ),
          {},
          remove(
            ri!apiVendors,
            rule!AS_CO_UT_whereContains(
              values: ri!refVendors[recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.federalIdentifier],
              array: ri!apiVendors[recordType!AS_GSS_Ref_Vendor_SYNCEDRECORD.federalIdentifier]
            )
          )
        )
      }
    )
  )
)
