a!localVariables(
  /* Return the entry-point rule if it exists, or else returns a no-op */
  /* Make sure you use a refreshAlways: true */
  local!rule: a!refreshVariable(
    value: rule!AS_FRM_getRuleReferenceOrNoOp(
      ruleName: "AS_GSM_ENTRYPOINT_GETDATA_validateVendor_Slg_v1"
    ),
    refreshAlways: true
  ),
  /* Actually execute the rule */
  local!rule(
    /* This should always be passed in every app reference rule */
    /* which returns a default value if the rule does not exist */
    ifNull_default: {},
    /* Entrypoint-specific inputs for that rule */
    vendor: ri!vendor
  )
)
