a!localVariables(
  local!evaluationStatus: rule!AS_CO_UT_queryRecord(
    recordType: recordType!AS_GSS_R_Data_SYNCEDRECORD,
    filters: {
      #"SYSTEM_SYSRULES_queryFilter"(
        field: recordType!AS_GSS_R_Data_SYNCEDRECORD.refType,
        operator: "=",
        value: cons!AS_GSS_REF_TYPE_EVALUATION_STATUS
      ),
      #"SYSTEM_SYSRULES_queryFilter"(
        field: recordType!AS_GSS_R_Data_SYNCEDRECORD.refDataId,
        operator: "not in",
        value: {
          cons!AS_GSS_REF_ID_EVALUATION_STATUS_TEMP,
          cons!AS_GSS_REF_ID_EVALUATION_STATUS_DELETED
        }
      ),
      #"SYSTEM_SYSRULES_queryFilter"(
        field: recordType!AS_GSS_R_Data_SYNCEDRECORD.isActive,
        operator: "=",
        value: true
      )
    },
    fields: {
      recordType!AS_GSS_R_Data_SYNCEDRECORD.refDataId,
      recordType!AS_GSS_R_Data_SYNCEDRECORD.refLabel
    },
    pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
      startIndex: 1,
      batchSize: cons!AS_CO_INT_QUERY_MAX_BATCH_SIZE
    ),
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET
  ).data,
  #"SYSTEM_SYSRULES_forEach"(
    items: local!evaluationStatus,
    expression: a!map(
      refDataId: fv!item[recordType!AS_GSS_R_Data_SYNCEDRECORD.refDataId],
      refLabel: fv!item[recordType!AS_GSS_R_Data_SYNCEDRECORD.refLabel],

    )
  )
)
