sail_code:
a!localVariables(
  local!data: rule!AS_GSS_UT_constructEvalVendorAndDocsForAwardCreation(
    evaluationId: ri!evaluationId,
    includeAllVendors: true(),
    fieldsToFetch: rule!AS_GSS_BL_recordFieldsToQueryForWinningVendorsBasicDetails()
  ),
  if(
    rule!AS_CO_UT_checkIsNullOrEmpty(value: local!data),
    a!map(),
    a!map(
      evalId: local!data.evaluationId,
      evaluationNumber: local!data.evaluationNumber,
      vendors: #"SYSTEM_SYSRULES_forEach"(
        items: local!data.vendor,
        expression: a!map(
          gsmVendorRefId: fv!item.gsmVendorRefId,
          vendorLegalName: fv!item.legalName,
          isWinningVendor: tointeger(
            rule!AS_CO_UT_indexPath(
              cdt: fv!item,
              field: "decisionType.refDataId",
              indexDefault: 0
            )
          ) = cons!AS_GSS_REF_ID_VENDOR_DECISION_SELECT,
          appianDocumentIds: rule!AS_CO_UT_rejectNullValuesFromArray(array: fv!item.documents.appianDocId)
        )
      )
    )
  )
)
