sail_code:
a!localVariables(
  local!searchTerm,
  {
    #"SYSTEM_SYSRULES_headingField"(
      text: "Search Vendors",
      size: "SMALL",
      headingTag: "H2",
      fontWeight: "BOLD"
    ),
    #"SYSTEM_SYSRULES_columnsLayout"(
      columns: {
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: rule!AS_CO_INP_textField(
            label: "Search Vendors",
            instructions: "Search by Name, TIN or FEIN",
            labelPosition: "COLLAPSED",
            value: local!searchTerm,
            saveInto: { local!searchTerm }
          ),
          width: "MEDIUM"
        ),
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: #"SYSTEM_SYSRULES_ButtonArrayLayout"(
            buttons: #"SYSTEM_SYSRULES_buttonWidget_v1"(
              label: if(
                cons!AS_GSS_TOGGLE_AUTOMATION_TESTING_ENABLED,
                "Search",
                ""
              ),
              icon: "search",
              accessibilityText: "Search",
              value: true(),
              saveInto: {
                ri!searchAction,
                a!save(ri!searchTerm, local!searchTerm),
                a!save(ri!returnedVendors, null),
                a!save(
                  ri!returnedVendors,
                  rule!AS_GSS_UT_finalizedApiAndRefVendorsRecordListForStateAndLocal(searchTerm: local!searchTerm)
                )
              },
              size: "SMALL",
              color: "SECONDARY",
              disabled: rule!AS_CO_UT_isBlank(value: local!searchTerm)
            ),
            align: "START",
            marginBelow: "NONE"
          )
        )
      },
      alignVertical: "TOP",
      marginBelow: "NONE",
      spacing: "DENSE"
    )
  }
)
