  a!isNullOrEmpty(value: ri!legalName),
  {},
  concat(
    rule!AS_CO_UT_replaceNull(
      nullableValue: upper(stripwith(ri!legalName, " ")),
      replacementValue: "N/A"
    ),
    "|",
    rule!AS_CO_UT_replaceNull(
      nullableValue: upper(ri!identifier1),
      replacementValue: "N/A"
    ),
    "|",
    rule!AS_CO_UT_replaceNull(
      nullableValue: upper(ri!identifier2),
      replacementValue: "N/A"
    )
  )
)
